public class AlphabetUtil {
    public static char CharacterToAlphabet(char ch) {
        if (ch >= 'a' && ch <= 'z') {
            return (char) ((ch + 65) - 97);
        }
        if (ch >= 'A' && ch <= 'Z') {
            return ch;
        }
        if (ch < '一' || ch > '龥') {
            return '@';
        }
        return "".charAt(ch - 19968);
    }

    public static String getCollationKey(String text) {
        return getCollationKey(text, true);
    }

    public static String getCollationKey(String text, boolean withPrefix) {
        if (text == null || text.length() == 0) {
            return "";
        }
        if (!withPrefix) {
            return text;
        }
        boolean isChinese = false;
        char ch = text.charAt(0);
        if (ch >= '一' && ch <= '龥') {
            isChinese = true;
        }
        if (isChinese) {
            return CharacterToAlphabet(text.charAt(0)) + text;
        }
        return text;
    }
}
